from time import sleepimport pytestfrom appium import webdriverclass TestXueqiu:    def setup_class(self):        caps = {}        caps["platformName"] = "android"        caps["deviceName"] = "demo"        caps["appPackage"] = "com.xueqiu.android"        caps["appActivity"] = ".view.WelcomeActivityAlias"        caps["autoGrantPermissions"] = "true"        caps["noReset"] = "true"        caps["dontStopAppOnReset"] = "true"        self.driver = webdriver.Remote("http://127.0.0.1:4723/wd/hub", caps)        self.driver.implicitly_wait(10)        self.driver.find_element_by_id("com.xueqiu.android:id/home_search").click()    @pytest.mark.parametrize('name, assert_data', [('xiaomi', '01810'), ('jd', 'JD'), ('alibaba', 'BABA')])    def test_xueqiu(self, name, assert_data):        self.driver.find_element_by_id("com.xueqiu.android:id/search_input_text").send_keys(name)        names = self.driver.find_element_by_id("com.xueqiu.android:id/code").text        assert assert_data in names        self.driver.find_element_by_id("com.xueqiu.android:id/action_delete_text").click()        # //*[@text='BABA']/../../../..//*[@text='加自选']   定位加自选    def teardown_class(self):        sleep(10)        self.driver.quit()